import Modal from './FormModal' 
import './AlbumListStyle.css'
const AlbumList = ({allAlbums}) => {
    const [selectedAlbum,setSelecteAlbum]=useSatate(0)   
    return (
        <div className="d-flex">
            {
                allAlbums.map((album)=>{
                    return <div class="card m-2" style={{width: "18rem"}} key={album.id}>
                        <img alt={album.name} class="card-img-top" src={album.images[0].url} />
                        <div class="card-body">
                            <h5 className="card-title">{album.name}</h5>
                            <p class="card-text">{album.label}</p>
                            <button type="button" onClick={()=>setSelecteAlbum(album.id)} class="btn btn-outline-secondary text-hover add-note-btn" data-bs-toggle="modal" data-bs-target="#exampleModal">
                            Add Note
                            </button>
                        </div>
                    </div>
                })
            }
            <Modal selectedAlbum={selectedAlbum}/>
        </div>
    )
}

export default AlbumList
